CREATE PROCEDURE [dbo].[sp_Mentor_RegisterBasicInfo]
@UserID INT, @Profession NVARCHAR (100)=NULL, @Specialization NVARCHAR (100)=NULL, @IsCurrentlyWorking BIT=NULL, @Division NVARCHAR (50)=NULL, @WorkAddress NVARCHAR (200)=NULL, @WorkPostalCode NVARCHAR (10)=NULL, @BusinessTelephone BIGINT=NULL, @ConsentToReceiveJVSEmail BIT=NULL, @Credentials_Degree NVARCHAR (100)=NULL, @ProfessionalAffiliations NVARCHAR (50)=NULL, @Other NVARCHAR (100)=NULL, @HowLongInProfessionInCAD INT=NULL, @WorkExperience NVARCHAR (2000)=NULL, @SpecialSkillsHobbiesOrInterests NVARCHAR (500)=NULL, @WhyVolunteer NVARCHAR (500)=NULL, @CurrentJobTitle NVARCHAR (50)=NULL
AS
INSERT INTO dbo.Mentor (UserID, Profession, Specialization, IsCurrentlyWorking, CurrentJobTitle, Division, WorkAddress, WorkPostalCode, BusinessTelephone, ConsentToReceiveJVSEmail, Credentials_Degree, ProfessionalAffiliations, Other, HowLongInProfessionInCAD, WorkExperience, SpecialSkillsHobbiesOrInterests, WhyVolunteer)
 VALUES (@UserID, @Profession, @Specialization, @IsCurrentlyWorking, @CurrentJobTitle, @Division, @WorkAddress, @WorkPostalCode, @BusinessTelephone, @ConsentToReceiveJVSEmail, @Credentials_Degree, @ProfessionalAffiliations, @Other, @HowLongInProfessionInCAD, @WorkExperience, @SpecialSkillsHobbiesOrInterests, @WhyVolunteer)